package edu.ucsb.cs56.drawings.jackjunliu.advanced;import java.awt.Graphics2D;import java.awt.Shape; // general class for shapesimport java.awt.Color; // class for Colorsimport java.awt.Stroke;import java.awt.BasicStroke;import edu.ucsb.cs56.drawings.utilities.ShapeTransforms;import edu.ucsb.cs56.drawings.utilities.GeneralPathWrapper;/** * A class with static methods for drawing various pictures *  * @author Phill Conrad, Jack Liu * @version for UCSB CS56, F17 */public class AllMyDrawings{    public static void drawPicture1(Graphics2D g2) {		Lollipop h1 = new Lollipop(100,250,50,50);	g2.setColor(Color.CYAN); g2.draw(h1);		// Make a black lollipop that's half the size, 	// and moved over 150 pixels in x direction		Shape h2 = ShapeTransforms.scaledCopyOfLL(h1,0.5,0.5);	h2 = ShapeTransforms.translatedCopyOf(h2,150,0);	g2.setColor(Color.BLACK); g2.draw(h2);		// Here's a lollipop that's 4x as big (2x the original)	// and moved over 150 more pixels to right.	h2 = ShapeTransforms.scaledCopyOfLL(h2,4,4);	h2 = ShapeTransforms.translatedCopyOf(h2,150,0);		// We'll draw this with a thicker stroke	Stroke thick = new BasicStroke (4.0f, BasicStroke.CAP_BUTT, BasicStroke.JOIN_BEVEL);       		Stroke orig=g2.getStroke();	g2.setStroke(thick);	g2.setColor(new Color(0x002FA7)); 	g2.draw(h2); 		// Draw two Swirly Lollipops		SwirlyLollipop hw1 = new SwirlyLollipop(50,10,40,20);	SwirlyLollipop hw2 = new SwirlyLollipop(200,350,200,100);		g2.draw(hw1);	g2.setColor(new Color(0x8F00FF)); g2.draw(hw2);		// Labeled Drawing		g2.setStroke(orig);	g2.setColor(Color.BLACK); 	g2.drawString("A few funny looking lollipops by", 20,20);	g2.drawString("Jack Liu - Fall 2017", 40,40);    }        public static void drawPicture2(Graphics2D g2) {		        Lollipop largeSmallStick = new Lollipop(100,50,225,150);        Lollipop smallLollipop = new Lollipop(20,50,40,30);	Lollipop smallLargeStick = new Lollipop(20,150,20,40);	Lollipop whatPop = new Lollipop(20,250,40,20);		g2.setColor(Color.RED);     g2.draw(largeSmallStick);	g2.setColor(Color.GREEN);   g2.draw(smallLollipop);	g2.setColor(Color.BLUE);    g2.draw(smallLargeStick);	g2.setColor(Color.MAGENTA); g2.draw(whatPop);	        Lollipop h1 = new Lollipop(100,250,50,75);	g2.setColor(Color.CYAN); g2.draw(h1);		// Make a black lollipop that's half the size, 	// and moved over 150 pixels in x direction	Shape h2 = ShapeTransforms.scaledCopyOfLL(h1,0.5,0.5);	h2 = ShapeTransforms.translatedCopyOf(h2,150,0);	g2.setColor(Color.BLACK); g2.draw(h2);		// Here's a lolipop that's 4x as big (2x the original)	// and moved over 150 more pixels to right.	h2 = ShapeTransforms.scaledCopyOfLL(h2,4,4);	h2 = ShapeTransforms.translatedCopyOf(h2,150,0);		// We'll draw this with a thicker stroke	Stroke thick = new BasicStroke (4.0f, BasicStroke.CAP_BUTT, BasicStroke.JOIN_BEVEL);       		Stroke orig=g2.getStroke();	g2.setStroke(thick);	g2.setColor(new Color(0x002FA7)); 	g2.draw(h2); 		// Draw two Swirly Lollipops		SwirlyLollipop hw1 = new SwirlyLollipop(50,350,40,75);	SwirlyLollipop hw2 = new SwirlyLollipop(200,350,200,100);		g2.draw(hw1);	g2.setColor(new Color(0x8F00FF)); 		// Rotate the Lollipop 45 degrees around middle of stick.	Shape hw3 = ShapeTransforms.rotatedCopyOf(hw2, Math.PI/4.0);		g2.draw(hw3);		g2.setStroke(orig);	g2.setColor(Color.BLACK); 	g2.drawString("A bunch of Lollipops and Swirly Lollipops for added flavor.", 20,20);	g2.drawString("by Jack Liu, Fall 2017", 40, 40);    }        public static void drawPicture3(Graphics2D g2) {		// label the drawing		g2.drawString("A bunch of just Swirly Lolipops", 20,20);	g2.drawString("by Jack Liu, Fall 2017", 40,40);			// Draw some Swirly Lollipops.	        SwirlyLollipop largePop = new SwirlyLollipop(100,50,225,150);	SwirlyLollipop smallStick = new SwirlyLollipop(20,50,40,30);		g2.setColor(Color.RED);     g2.draw(largePop);	g2.setColor(Color.GREEN);   g2.draw(smallStick);	    }       }